by_chan.map:
function(doc) {
  if (doc.timestamp && doc.event)
    emit([doc.channel, doc.timestamp], doc.event);
}

by_user.map:
function(doc) {
  if (doc.timestamp && doc.event)
    emit([doc.user, doc.channel, doc.timestamp], doc.event);
}

by_nick.map:
function(doc) {
  if (doc.timestamp && doc.event) {
    emit([doc.event[1], doc.channel, doc.timestamp], doc.event);
    if (doc.event[0] == 'nick' || doc.event[0] == 'kick')
      emit([doc.event[2], doc.channel, doc.timestamp], doc.event);
    if (doc.event[0] == 'mode')
      emit([doc.event[3], doc.channel, doc.timestamp], doc.event);
  }
}
